'use strict';

var invalid = [];
var test;

test = {
	'code': [
		'/**',
		'* Single require without proper spacing.',
		'*',
		'* @example',
		'* var foo = require( \'foo\' );',
		'* var x = foo();',
		'*/',
		'function test1() {}'
	].join( '\n' ),
	'errors': [
		{
			'message': 'Missing empty line between require statement and code in JSDoc example',
			'type': 'FunctionDeclaration'
		}
	]
};
invalid.push( test );

test = {
	'code': [
		'/**',
		'* Multiple requires without proper spacing.',
		'*',
		'* @example',
		'* var foo = require( \'foo\' );',
		'* var bar = require( \'bar\' );',
		'* var x = foo( bar() );',
		'*/',
		'function test2() {}'
	].join( '\n' ),
	'errors': [
		{
			'message': 'Missing empty line between require statement and code in JSDoc example',
			'type': 'FunctionDeclaration'
		}
	]
};
invalid.push( test );

test = {
	'code': [
		'/**',
		'* Mixed spacing - second require without proper spacing.',
		'*',
		'* @example',
		'* var foo = require( \'foo\' );',
		'*',
		'* var bar = require( \'bar\' );',
		'* var x = foo( bar() );',
		'*/',
		'function test4() {}'
	].join( '\n' ),
	'errors': [
		{
			'message': 'Missing empty line between require statement and code in JSDoc example',
			'type': 'FunctionDeclaration'
		}
	]
};
invalid.push( test );


// EXPORTS //

module.exports = invalid;
