'use strict';

var valid = [];
var test;

test = {
	'code': [
		'/**',
		'* Single require with proper spacing.',
		'*',
		'* @example',
		'* var foo = require( \'foo\' );',
		'*',
		'* var x = foo();',
		'*/',
		'function test1() {}'
	].join( '\n' )
};
valid.push( test );

test = {
	'code': [
		'/**',
		'* Multiple requires with proper spacing.',
		'*',
		'* @example',
		'* var foo = require( \'foo\' );',
		'*',
		'* var bar = require( \'bar\' );',
		'*',
		'* var x = foo( bar() );',
		'*/',
		'function test2() {}'
	].join( '\n' )
};
valid.push( test );

test = {
	'code': [
		'/**',
		'* Example without requires.',
		'*',
		'* @example',
		'* var x = 5;',
		'* var y = x * 2;',
		'*/',
		'function test3() {}'
	].join( '\n' )
};
valid.push( test );

test = {
	'code': [
		'/**',
		'* Example with requires and comments.',
		'*',
		'* @example',
		'* var foo = require( \'foo\' );',
		'*',
		'* // This is a comment',
		'*',
		'* var x = foo();',
		'*/',
		'function test4() {}'
	].join( '\n' )
};
valid.push( test );


// EXPORTS //

module.exports = valid;
