'use strict';

// MODULES //

var cmul = require( '@stdlib/complex/float32/base/mul' );
var cadd = require( '@stdlib/complex/float32/base/add' );
var conj = require( '@stdlib/complex/float32/conj' );
var Complex64 = require( '@stdlib/complex/float32/ctor' );


// MAIN //

/**
* Computes the dot product of `x` and `y`.
*
* @param {integer} N - number of indexed elements
* @param {Complex64Array} x - first input array
* @param {integer} strideX - `x` stride length
* @param {NonNegativeInteger} offsetX - starting index for `x`
* @param {Complex64Array} y - second input array
* @param {integer} strideY - `y` stride length
* @param {NonNegativeInteger} offsetY - starting index for `y`
* @returns {Complex64} dot product
*
* @example
* var Complex64Array = require( '@stdlib/array/complex64' );
* var Complex64 = require( '@stdlib/complex/float32/ctor' );
* var realf = require( '@stdlib/complex/float32/real' );
* var imagf = require( '@stdlib/complex/float32/imag' );
*
* var x = new Complex64Array( [ 7.0, -8.0, -1.0, -9.0 ] );
* var y = new Complex64Array( [ 6.0, -6.0, -9.0, 5.0 ] );
*
* var out = cdotc( x.length, x, 1, 0, y, 1, 0 );
* // returns <Complex64>
*
* var re = realf( out );
* // returns 54
*
* var im = imagf( out );
* // returns -80
*/
function cdotc( N, x, strideX, offsetX, y, strideY, offsetY ) {
	var conjX;
	var dot;
	var ix;
	var iy;
	var i;

	dot = new Complex64( 0, 0 );
	if ( N <= 0 ) {
		return dot;
	}
	ix = offsetX;
	iy = offsetY;
	for ( i = 0; i < N; i++ ) {
		conjX = conj( x.get( ix ) );
		dot = cadd( dot, cmul( conjX, y.get( iy ) ) );
		ix += strideX;
		iy += strideY;
	}
	return dot;
}


// EXPORTS //

module.exports = cdotc;
