'use strict';

// MODULES //

var float64ToFloat32 = require( '@stdlib/number/float64/base/to-float32' );
var float32Array = require( '@stdlib/array/float32' );
var assign = require( './assign.js' );


// MAIN //

/**
* Returns the single-precision minimum and maximum values.

* @param {number} x - first number
* @param {number} y - second number
* @returns {Array<number>} minimum and maximum values
*
* @example
* var v = minmaxf( 3.0, 4.0 );
* // returns <Float32Array>[ 3.0, 4.0 ]
*
* @example
* var v = minmaxf( 3.14, NaN );
* // returns <Float32Array>[ NaN, NaN ]
*
* @example
* var v = minmaxf( +0.0, -0.0 );
* // returns <Float32Array>[ -0.0, 0.0 ]
*/
function minmaxf( x, y ) {
	var out = new float32Array( 2 );
	x = float64ToFloat32( x );
	y = float64ToFloat32( y );
	return assign( x, y, out, 1, 0 );
}


// EXPORTS //

module.exports = minmaxf;
