#include "stdlib/math/base/special/minmaxf.h"
#include "stdlib/math/base/assert/is_nanf.h"
#include "stdlib/math/base/assert/is_negative_zerof.h"

/**
* Evaluates the single-precision min and max value.
*
* @param x      first input
* @param y      second input
* @param out 	output array
* @return		void
*
* @example
* float out = [ 0.0, 0.0 ];
* double v = stdlib_base_minmaxn( 3.2, 4.1, out );
*
* @example
* double out = [ 0.0, 0.0 ];
* double v = stdlib_base_minmaxn( 4.0, 0.0/0.0, out );
*/
void stdlib_base_minmaxf( const float x, const float y, float* min, float* max ){
	if ( stdlib_base_is_nanf( x ) || stdlib_base_is_nanf( y ) ) {
		*min = 0.0f / 0.0f;
		*max = 0.0f / 0.0f;
		return;
	}
	if ( x == y && x == 0.0 ) {
		if ( stdlib_base_is_negative_zerof( x ) ) {
			*min = x;
			*max = y;
			return;
		}
		*min = y;
		*max = x;
		return;
	}
	if ( x < y ) {
		*min = x;
		*max = y;
		return;
	}
	*min = y;
	*max = x;
	return;
}
