#include "stdlib/math/base/special/minmaxn.h"
#include "stdlib/math/base/assert/is_nan.h"
#include "stdlib/math/base/assert/is_negative_zero.h"
#include "stdlib/math/base/assert/is_positive_zero.h"
#include "stdlib/constants/float64/ninf.h"
#include "stdlib/constants/float64/pinf.h"

/**
* Evaluates the min and max of n values.
*
* @param out       array reference of double for output
* @param args      array reference of double for arguments
* @param al 	   length of the args array
* @return          void
*
* @example
* double out = [ 0.0, 0.0 ];
* double args = [ 3.14, -4.2 ];
* double v = stdlib_base_minmaxn( out, args );
*
* @example
* double out = [ 0.0, 0.0 ];
* double args = [ 3.14, -4.2, 0.0/0.0 ];
* double v = stdlib_base_minmaxn( out, args );
* // returns +0.0
*/
void stdlib_base_minmaxn( double *out, double *args, signed long int al ){
	double v;
	int i;
	
	if ( al == 1 ){
		out[ 0 ] = args[ 0 ];
		out[ 1 ] = args[ 0 ];
		return;
	}
	if ( al == 2 ){
		if ( stdlib_base_is_nan( args[ 0 ] ) || stdlib_base_is_nan( args[ 1 ] ) ) {
			out[ 0 ] = 0.0 / 0.0;
			out[ 1 ] = 0.0 / 0.0;
			return;
		}
		if( args[ 0 ] == args[ 1 ] && args[ 0 ] == 0 ) {
			if( stdlib_base_is_negative_zero( args[ 0 ] ) ){
				out[ 0 ] = args[ 0 ];
				out[ 1 ] = args[ 1 ];
				return;
			}
			out[ 0 ] = args[ 1 ];
			out[ 1 ] = args[ 0 ];
		}
		if ( args[ 0 ] < args[ 1 ] ) {
			out[ 0 ] = args[ 0 ];
			out[ 1 ] = args[ 1 ];
			return;
		}
		out[ 0 ] = args[ 1 ];
		out[ 1 ] = args[ 0 ];
		return;

	}
	out[ 0 ] = STDLIB_CONSTANT_FLOAT64_PINF;
	out[ 1 ] = STDLIB_CONSTANT_FLOAT64_NINF;
	for ( i = 0; i < al; i++ ){
		v = args[ i ];
		if ( stdlib_base_is_nan( v ) ) {
			out[ 0 ] =  0.0 / 0.0;
			out[ 1 ] =  0.0 / 0.0;
			return;
		}
		if ( v < out[ 0 ] ) {
			out[ 0 ] = v;
		} else if (
			v == 0 &&
			v == out[ 0 ] &&
			stdlib_base_is_negative_zero( v )
		) {
			out[ 0 ] = v;
		}
		if ( v > out[ 1 ] ) {
			out[ 1 ] = v;
		} else if (
			v == 0 &&
			v == out[ 1 ] &&
			stdlib_base_is_positive_zero( v )
		) {
			out[ 1 ] = v;
		}
	}
	return;
}
