'use strict';

// MODULES //

var isNumber = require( '@stdlib/assert/is-number' ).isPrimitive;
var format = require( '@stdlib/string/format' );


// MAIN //

/**
* Sets the rendering mode.
*
* @private
* @param {Array} num - num indicate scale value of the height.
* @throws {TypeError} must be a number
* @throws {RangeError} must be in range of 0 to 1
*/
function set( num ) {
	/* eslint-disable no-invalid-this */
	if ( !num ) {
		this._cols = process.stdout.columns;
		return;
	}
	if ( !isNumber( num ) ) {
		throw new TypeError( format( 'invalid assignment.  must be a array.' ) );
	}
	this._cols = num;
}


// EXPORTS //

module.exports = set;
