'use strict';

// MODULES //

var isNumber = require( '@stdlib/assert/is-number' ).isPrimitive;
var format = require( '@stdlib/string/format' );


// MAIN //

/**
* Sets the top pading.
*
* @private
* @param {number} num - number of character pading from top.
* @throws {TypeError} must be a number
*/
function set( num ) {
	/* eslint-disable no-invalid-this */
	if ( !isNumber( num ) ) {
		throw new TypeError( format( 'invalid assignment. `%s` must be a number. Value: `%s`.', 'autoRender', num ) );
	}
	this._padTop = num;
}


// EXPORTS //

module.exports = set;
