function line ( sx, sy ) {
    if ( sx.length < 2 ) {
        return;
    }
    var lcord =[];
    var slop;
    var mx;
    var Mx;
    var my;
    var My;
    var i;

    mx = Math.min( sx[0], sx[1] );
    Mx = Math.max( sx[0], sx[1] );
    my = Math.min( sy[0], sy[1] );
    My = Math.max( sy[0], sy[1] );
    if ( Mx-mx == 0 ) {
        for( i = my; i <= My; i++ ){
            lcord.push([ mx, i]);
        }
        return lcord;
    }
    if ( My-my== 0 ) {
        for( i = mx; i <= Mx; i++  ) {
            lcord.push( [ i, my] );
        }
        return lcord;
    }
    slop = (My-my)/(Mx-mx);
    for( i = mx; i <= Mx; i++ ) {
        lcord.push([ i, Math.floor( i * slop )]);
    }
    return lcord;
}