function scale_for_canva (elms, bins, lim, pad) {
    var elm;
    var arr=[];
    var i;
    for ( i = 0; i < elms.length; i++ ) {
        elm = elms[i];
        arr.push(Math.floor( pad + Math.round( (bins - 1) * ((elm - lim[0]) / (lim[1] - lim[0])))))
    }
    return arr;
}

module.exports = scale_for_canva;